example.code:
// web.config 의 system.web/httpHandlers 에 등록한다. <add verb="GET" path="FusionResourceFile.axd" type="NSoft.NFramework.FusionCharts.Web.ResourceFileHandler, NSoft.NFramework.FusionCharts" validate="false" />
// web page에 FusionChart 용 control 정의 <%@ Register Assembly="NSoft.NFramework.FusionCharts" Namespace="NSoft.NFramework.FusionCharts.Web" TagPrefix="rcl" %>
<rcl:FusionChart ID="dynamicChart" runat="server" FileName="Gantt.swf" ChartId="gantt2" DataUrl="~/Widgets/Gantt/DataProviders/SampleDataProvider.ashx" Width="800" Height="500" DebugMode="false" />

// web.config의 system.web/httpHandlers 에 추가하면 됩니다.
<!-- NOTE : Fusion Chart 를 Image로 변환하는 Handler 입니다.-->
<add verb="*" path="NSoft.NFramework.FusionCharts.Export.axd" type="NSoft.NFramework.FusionCharts.Web.ExportHandler, NSoft.NFramework.FusionCharts" validate="false"/>

// web.config 의 system.web/httpHandlers 에 등록한다.
<add verb="GET" path="NSoft.NFramework.FusionCharts.ResourceFile.axd" type="NSoft.NFramework.FusionCharts.Web.ResourceFileHandler, NSoft.NFramework.FusionCharts" validate="false" />

// Line.swf 파일을 리소스에서 읽어서, 다운로드한다.
FusionResourceFile.axd?File=Line.swf

// IIS 6.0 이하 classic 의 web.config/system.web/httpHandlers section에 다음을 추가해야 합니다.
<add verb="*" path="NSoft.NFramework.FusionCharts.ResourceFile.axd" type="NSoft.NFramework.FusionCharts.Web.ResourceFileHandler, NSoft.NFramework.FusionCharts" />

// IIS 7.0 이상 web.config/system.webServer/httpHandlers section에 다음을 추가해야 합니다.
<add name="NSoft.NFramework_FusionCharts_ResourceFile_axd" verb="*" path="NSoft.NFramework.FusionCharts.ResourceFile.axd" type="NSoft.NFramework.FusionCharts.Web.ResourceFileHandler, NSoft.NFramework.FusionCharts" />

// 코드상에서
// NSoft.NFramework.FusionCharts.ResourceFile.axd?File=FusionCharts.js
ResourceFileHandler + "?File=" + filename;

// IIS 6.0 이하 classic 의 web.config/system.web/httpHandlers section에 다음을 추가해야 합니다.
<add verb="*" path="NSoft.NFramework.FusionCharts.Export.axd" type="NSoft.NFramework.FusionCharts.Web.ExportHandler, NSoft.NFramework.FusionCharts" validate="false"/>

// IIS 7.0 이상 web.config/system.webServer/httpHandlers section에 다음을 추가해야 합니다.
<add name="NSoft.NFramework_FusionCharts_Export_axd" verb="*" path="NSoft.NFramework.FusionCharts.Export.axd" type="NSoft.NFramework.FusionCharts.Web.ExportHandler, NSoft.NFramework.FusionCharts"/>

// 코드상에서
chart.ExportAttr.ExportHandler =  HandlerPath.ExportHandler
